import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useState, useRef } from 'react';
import { Text, Image, StyleSheet, View, Platform, Dimensions } from 'react-native';
import { TouchableOpacity, ScrollView, TextInput } from 'react-native-gesture-handler';
import { SafeAreaView } from 'react-native-safe-area-context';
import BottomSheet from 'react-native-gesture-bottom-sheet';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import { commonStyles } from '../../styles/common';
import MapView,  { PROVIDER_GOOGLE } from 'react-native-maps';

import COLORS from '../../styles/colors';

type MapScreenProp = NativeStackScreenProps<NavigationStackParams, 'Map'>;

const { height } = Dimensions.get('window');

const styles = StyleSheet.create({
  headerContainer: {
    position: 'absolute',
    top: 59,
    left: 26,
  },
  headerTitle: {
    position: 'absolute',
    fontFamily: 'Pretendard-Bold',
    fontSize: 20,
  },
  headerNotice: {
    position: 'absolute',
    width: 24,
    height: 24,
    left: 295,
  },
  headerMyPage: {
    position: 'absolute',
    width: 24,
    height: 24,
    left: 335,
  },

  //
  countryCodeSelectorTitleText: {
    position: 'absolute',
    marginTop: 66,
    marginStart: 26,
    marginBottom: 40,
    fontFamily: 'Pretendard-Bold',
    fontSize: 20,
  },
  // countryCodeSelectorWrapper: {
  //   flex: 1,
  //   flexDirection: 'row',
  //   paddingHorizontal: 26,
  //   paddingVertical: 20,
  //   alignItems: 'center',
  // },
  button: {
    position: 'absolute',
    height: 50,
    width: 150,
    backgroundColor: "#140078",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 20,
    shadowColor: "#8559da",
    shadowOpacity: 0.7,
    shadowOffset: {
      height: 4,
      width: 4,
    },
  },
  absolute: {
    position: 'absolute',
    bottom: 110,
  }
})

const IcNotice = require('../../../assets/ic-bell.png');
const IcMyPage = require('../../../assets/ic-user-circle.png'); 

function MapScreen({ navigation }: MapScreenProp) {
  const [selectedCountry, setSelectedCountry] = useState(true);
  const bottomSheetRef = useRef<any>(null);

  return (
    <View>

      <View style={{ width: '100%', height: '100%'}}>
        {/* Google Map */}
        {/* <MapView 
          style={{ width: '100%', height: '100%'}} 
          provider={PROVIDER_GOOGLE}
          initialRegion={{ latitude: 35.91395373474155, longitude: 127.73829440215488, latitudeDelta: 5, longitudeDelta: 5,}} 
        /> */}


        {/* Header */}
        <View style={styles.headerContainer}>
          <Text style={styles.headerTitle}>마음 거리</Text>

          <TouchableOpacity onPress={() => navigation.navigate('Notice')}>
            <Image source={IcNotice} style={styles.headerNotice}></Image>
          </TouchableOpacity>

          <TouchableOpacity onPress={() => navigation.navigate('MyPage')}>
            <Image source={IcMyPage} style={styles.headerMyPage} ></Image>
          </TouchableOpacity>
        </View>


        {/* Bottom Sheet */}
        <BottomSheet ref={bottomSheetRef} snapPoints={[700]} height={height - 93} style={styles.absolute} >
          <ScrollView>
            <TouchableOpacity style={styles.button} onPress={() => bottomSheetRef.current.show()}>
              <Text>111</Text>
              <Text>222</Text>
            </TouchableOpacity>
          </ScrollView>
        </BottomSheet>
      </View>


      {/* <Text onPress={() => navigation.navigate('MapDetail')}>상세 보기</Text> */}
      </View>
  );
}
