import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import { Button, Text, View, Image, StyleSheet } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import ButtonBack from '../../components/ButtonBack';
// import greetIcon from '../../../assets/greetIcon.png'

import COLORS from '../../styles/colors'

type GreetScreenProp = NativeStackScreenProps<NavigationStackParams, 'Greet'>;

// interface Btn {
//   style: StyleSheet,
//   title: String,
//   onPress: () => void,
// }

function GreetScreen({ navigation }: GreetScreenProp) {
  return (
    <SafeAreaView style={styles.container}>
      <ButtonBack onPress={() => navigation.goBack()} />
      <View style={layout.header}>
        <Text style={styles.mainTxt}>  user1님 웨일던에서 {"\n"}  가족과 더 가까워져 보세요!</Text>
      </View>

      <View style={layout.content}>
        <Image style={styles.img} source={require('../../../assets/greetIcon.png')}></Image>
        <Text style={styles.txt}>쉿-! 웨일던은 가족과만 {"\n"}대화할 수 있는 비밀 공간이에요</Text>
      </View>

      <View style={layout.footer}>
        <Button
          // style={styles.nextBtn}
          title="다음"
          onPress={() => navigation.navigate('GroupCodeInput')}
        />
      </View>
    </SafeAreaView>
  );
}

const layout = StyleSheet.create({
  header: {
    flex: 1,
    // backgroundColor: '#000',
    
  },
  content: {
    flex: 3,
    // backgroundColor: 'red',
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer: { 
    flex: 1,
    // backgroundColor: 'green',
  },
});


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: COLORS.BLUE_300,

    // justifyContent: 'center',
  },
  mainTxt: { 
    // position: 'relative',
    // display: 'flex',
    width: 311,
    height: 58,
    left: 10,
    top: 20,
    color: '#fff',
    // fontFamily: 'Pretendard',
    fontStyle: 'normal',
    fontWeight: 'bold',
    fontSize: 20,
    lineHeight: 24,
    flexGrow: 0,
    margin: 10,
  },
  img: { 
    // display: 'flex',
    // flex: 2,
    width: 297,
    height: 216,
    alignItems: 'center',
    justifyContent: 'center',
    // left: 39,
    // top: 267,
    // alignItems: 'center',
    // justifyContent: 'center',
  },
  txt: {
    width: 200,
    height: 48,
    color: '#fff',
    fontFamily: 'Pretendard',
    fontStyle: 'normal',
    fontWeight: 'normal',
    fontSize: 16,
    // alignItems: 'center',
    // justifyContent: 'center',
  },
  nextBtn : {
    flex: 1,
    backgroundColor: COLORS.BLUE_500,
    borderRadius: 5,
    padding: 10,
  },
});

const typography = StyleSheet.create({
  header: {
    color: "#61dafb",
    fontSize: 30,
    marginBottom: 36
  }
});

const flattenStyle = StyleSheet.flatten([
  styles.container,
  typography.header
]);

export default GreetScreen;
