import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import { Text, StyleSheet, Share, TouchableOpacity, View } from 'react-native';
import { SafeAreaView, SafeAreaProvider } from 'react-native-safe-area-context';
import { NavigationActions, StackActions } from 'react-navigation';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import ButtonBack from '../../components/ButtonBack';

type GroupCodeShareScreenProp = NativeStackScreenProps<
  NavigationStackParams,
  'GroupCodeShare'
>;


function GroupCodeShareScreen() {
  const code = '000ABC';
  const onSharePressed = () => {
    try {
      Share.share({
        message: `초대 코드는 ${code}입니다.`,
      });
    } catch (error) {
      alert(error);
    }
  };
  return (
    <SafeAreaProvider>
      <SafeAreaView style={styles.safeAreaContainer}>
        <View style={styles.container}>
          <View>
            <View style={styles.textContainer}>
              <Text style={styles.textTitle}>초대 코드를</Text>
              <Text style={styles.textTitle}>가족과 공유해볼까요?</Text>
            </View>
            <View style={styles.codeContainer}>
              <Text style={styles.textCode}>{code}</Text>
              <Text style={styles.textCodeDescription}>
                초대 코드는 48시간 유효합니다
              </Text>
            </View>
          </View>
          <TouchableOpacity style={styles.btnShare} onPress={onSharePressed}>
            <Text style={styles.textButtonShare}>초대 코드 공유하기</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    </SafeAreaProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    justifyContent: 'space-between',
    paddingHorizontal: 16,
  },
  safeAreaContainer: {
    flex: 1,
  },
  textTitle: {
    fontSize: 20,
    lineHeight: 30,
    fontWeight: '600',
  },
  textContainer: {
    marginTop: 49,
    paddingVertical: 20,
    paddingHorizontal: 10,
  },
  codeContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#446CFF',
    paddingVertical: 28,
    borderRadius: 10,
  },
  textCode: {
    color: 'white',
    fontWeight: '600',
    fontSize: 32,
    letterSpacing: 9,
  },
  textCodeDescription: {
    color: 'white',
    fontSize: 14,
    marginTop: 12,
  },
  textButtonShare: {
    color: 'white',
    textAlign: 'center',
  },
  btnShare: {
    textAlign: 'center',
    backgroundColor: '#446CFF',
    paddingVertical: 17,
    color: 'white',
    borderRadius: 5,
  },
});

export default GroupCodeShareScreen;
