import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import { Button, Text, View, StyleSheet } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { NavigationActions, StackActions } from 'react-navigation';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import ButtonBack from '../../components/ButtonBack';
import COLORS from '../../styles/colors'

type GroupCodeInputScreenProp = NativeStackScreenProps<
  NavigationStackParams,
  'GroupCodeInput'
>;

function GroupCodeInputScreen({ navigation }: GroupCodeInputScreenProp) {
  return (
    <SafeAreaView style={styles.container}>
      <ButtonBack onPress={() => navigation.goBack()} />
      <View style={layout.header}>
        <Text style={styles.mainTxt}>웨일던에 입장해 볼까요?{"\n"}초대 코드를 입력하세요</Text>
      </View>
      <Button
        title="입장하기"
        onPress={() =>
          navigation.dispatch(
            StackActions.reset({
              index: 0,
              actions: [NavigationActions.navigate({ routeName: 'Main' })],
            }),
          )
        }
      />
      <Text style={styles.groupCodeTxt}>아직 초대 코드가 없으신가요?</Text>
      <Button
        title="새로운 초대 코드 만들기"
        onPress={() => navigation.navigate('GroupCodeCreate')}
      />
      
    </SafeAreaView>
  );
}

const layout = StyleSheet.create({
  header: {
    flex: 1,
    // backgroundColor: '#000',
    
  },
  content: {
    flex: 3,
    // backgroundColor: 'red',
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer: { 
    flex: 1,
    // backgroundColor: 'green',
  },
});

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  mainTxt: {
    width: 311,
    height: 58,
    left: 10,
    top: 20,
    color: '#161D24',
    fontFamily: 'Pretendard',
    fontStyle: 'normal',
    fontWeight: 'bold',
    fontSize: 20,
    lineHeight: 24,
    // flex: 'none',
    flexGrow: 0,
    margin: 10,
  },
  groupCodeTxt: { 
    width: 163,
    height: 17,
    left: 0,
    top: 0,
    
    alignItems: 'center',
    justifyContent: 'center',
  },
})

export default GroupCodeInputScreen;
