import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useState } from 'react';
import { 
  Dimensions,
  Image,
  StyleSheet,
  Text,
  View,
  TextInput, } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { SafeAreaView } from 'react-native-safe-area-context';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import { commonStyles } from '../../styles/common';
import ButtonBack from '../../components/ButtonBack';
import COLORS from '../../styles/colors';

type HomeScreenProp = NativeStackScreenProps<NavigationStackParams, 'Home'>;

const Logo = require('../../../assets/logo-whaledone.png')
const IcStudy = require('../../../assets/ic-study.png');
const IcRelationship = require('../../../assets/ic-relationship.png');
const IcDaily = require('../../../assets/ic-daily.png');
const IcHealth = require('../../../assets/ic-health.png');
const IcTmi = require('../../../assets/ic-tmi.png');

function HomeScreen({ navigation }: HomeScreenProp) {
  return (
    <SafeAreaView style={commonStyles.container}>
      <View style={styles.headerContainer}>
        <ButtonBack onPress={() => navigation.goBack()} />
        <Text style={styles.headerTitle}>일상 공유 제목</Text>
        <TouchableOpacity
          onPress={() => navigation.push('Main', { screen: 'Feed' })}
        >
          <Text
            style={[
              styles.uploadText,
            ]}
          >
            업로드
          </Text>
        </TouchableOpacity>
      </View>



      <Text>Home</Text>
      <Text onPress={() => navigation.navigate('Notice')}>알림</Text>
      <Text onPress={() => navigation.navigate('Record')}>일상 공유</Text>
      <Text onPress={() => navigation.navigate('MyPage')}>마이페이지</Text>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  headerTitle: {
    flex: 1,
    marginStart: 8,
    fontFamily: 'Pretendard-Bold',
    fontSize: 18,
  },
  uploadText: {
    fontFamily: 'Pretendard-Bold',
    fontSize: 16,
  },
});

export default HomeScreen;
