import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useState } from 'react';
import { 
  Dimensions,
  Image,
  StyleSheet,
  Text,
  View,
  TextInput } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { SafeAreaView } from 'react-native-safe-area-context';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import { commonStyles } from '../../styles/common';

import COLORS from '../../styles/colors';

type HomeScreenProp = NativeStackScreenProps<NavigationStackParams, 'Home'>;

const Logo = require('../../../assets/logo-whaledone.png');
const IcNotice = require('../../../assets/ic-bell.png');
const IcMyPage = require('../../../assets/ic-user-circle.png'); 
const IcWork = require('../../../assets/ic-work.png');
const IcRelationship = require('../../../assets/ic-relationship.png');
const IcDaily = require('../../../assets/ic-daily.png');
const IcHealth = require('../../../assets/ic-health.png');
const IcTmi = require('../../../assets/ic-tmi.png');

const styles = StyleSheet.create({
  headerContainer: {
    flexDirection: 'row',
    // justifyContent: 'space-between',
    alignItems: 'center',
    // height: 54,
  },
  headerLogo: {
    // flex: 1,
    marginStart: 8,   
    width: 118,
    height: 27,
    marginRight: 190,
  },
  headerNotice: {
    width: 24,
    height: 24,
    marginRight: 19,
  },
  headerMyPage: {
    width: 24,
    height: 24,
  },
  uploadText: {
    fontFamily: 'Pretendard-Bold',
    fontSize: 16,
  },
});

function HomeScreen({ navigation }: HomeScreenProp) {
  return (
    <SafeAreaView style={commonStyles.container}>
      <View style={styles.headerContainer}>
        <Image source={Logo} style={styles.headerLogo}></Image>
        <Image source={IcNotice} style={styles.headerNotice}></Image>
        <Image source={IcMyPage} style={styles.headerMyPage}></Image>
      </View>

      <View>
        <Text>뫄뫄한 토요일</Text>
        <Text>솨솨하세요</Text>
      </View>



      <Text>Home</Text>
      <Text onPress={() => navigation.navigate('Notice')}>알림</Text>
      <Text onPress={() => navigation.navigate('Record')}>일상 공유</Text>
      <Text onPress={() => navigation.navigate('MyPage')}>마이페이지</Text>
    </SafeAreaView>
  );
}



export default HomeScreen;
