import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import { Button, Text } from 'react-native';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import { NavigationStackParams } from '../../../infrastructures/types/NavigationStackParams';
import ButtonBack from '../../components/ButtonBack';

type GroupCodeCreateScreenProp = NativeStackScreenProps<
  NavigationStackParams,
  'GroupCodeCreate'
>;

function GroupCodeCreateScreen({ navigation }: GroupCodeCreateScreenProp) {
  const codeCreater = () => {
    var rawCode = Math.random().toString(36).substr(2,6)
    var code = [];
    for(var i=0; i<rawCode.length; i++){
      code[i] = rawCode.charAt(i)+'/'
    }

    return code
  };
  const res = codeCreater();

  return (
    <SafeAreaView>
      <ButtonBack onPress={() => navigation.goBack()} />
      <Text>GroupCodeCreate - {res}</Text>
      <Button
        title="초대 코드 공유하기"
        onPress={() => navigation.navigate('GroupCodeShare')}
      />
    </SafeAreaView>
  );
}

export default GroupCodeCreateScreen;
